import { FC, ReactNode, useEffect, useState } from "react"

interface FadeProps {
    children: ReactNode
}
export const VisibleComponent: FC<FadeProps> = ({ children }) => {
    const [isVis, setVis] = useState<boolean>(false);

    useEffect(() => {
        document.addEventListener('scroll', handleScroll)

        return () => {
            document.removeEventListener('scroll', handleScroll)
        }
    }, [])

    function handleScroll(event: Event) {
        const target = event.target as Document
        console.log('scrollHeight', target.documentElement.scrollHeight);
        console.log('scrollHeight', target.documentElement.scrollTop)
        console.log('scrollHeight', window.innerHeight)
    }

    return <div>{isVis && children}</div>
}
