import React, { useRef, useState, useEffect } from 'react';
import { useIntersectionObserver } from 'react-intersection-observer';

function VisibleComponent({ children }) {
  const ref = useRef(null);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const { observe, unobserve } = useIntersectionObserver({
      root: null, // весь документ
      rootMargin: '0px',
      threshold: 0.5, // видимость от 50%
    });

    const handleIntersection = (entries) => {
      if (entries[0].isIntersecting) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
        unobserve(ref.current); // Удаляем наблюдателя
      }
    };

    if (ref.current) {
      observe(ref.current, handleIntersection);
    }

    return () => {
      unobserve(ref.current);
    };
  }, []);

  if (!isVisible) {
    return null; // Удаляем компонент из DOM
  }

  return (
    <div ref={ref}>
      {children}
    </div>
  );
}

export default VisibleComponent;
