import axios from "axios";
import { useEffect, useState } from "react";

interface IUser {
  id: number,
  name: string,
  username: string,
}

function App() {
  const [users, setUsers] = useState<IUser[] | null>(null);
  const [limit, setLimit] = useState<number>(2);
  const [fetching, isFetching] = useState<boolean>(false);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    function handleScroll(event: any) {
      if (event.target.documentElement.scrollHeight - (event.target.documentElement.scrollTop + window.innerHeight)) {
        isFetching(true)
      }
    }

    return () => {
      window.removeEventListener('scroll', handleScroll);
    }
  }, [])

  useEffect(() => {
    if (fetching) {
      (async () => {
        const responese = (await axios.get(`https://jsonplaceholder.typicode.com/users?_limit=${limit}`)).data;
        setUsers(responese)
      })()
    }
  }, [users])

  return (
    <>
      {users
        &&
        <ul>
          {users.map(({ id, name, username }) => {
            return <li style={{ marginBottom: '150px' }} key={id}>Name: {name}, Username: {username}</li>
          })}
        </ul>
      }
    </>
  );
}

export default App;
