import { FC, ReactNode, useState, useEffect } from "react";
import './fade.css';

interface FadeProps {
    children: ReactNode,
}

export const Fade: FC<FadeProps> = ({ children }) => {
    const [isRender, setRender] = useState<boolean>(false);
    useEffect(() => {
        const element = document.getElementById('component');
        if (element) {
            const rect = element.getBoundingClientRect();
            if (rect.top >= 0 && rect.left >= 0 && rect.bottom <= globalThis.innerHeight && rect.right <= globalThis.innerWidth) {
                console.log('Компонент виден');
            } else {
                console.log('Компонент не виден');
            }
        }
    }, []);
    return <div className="container container_fade">{isRender && children}</div>
}
