import { FC, ReactNode, useEffect, useRef, RefObject } from "react"

interface FadeProps {
    children: ReactNode
}
export const VisibleComponent: FC<FadeProps> = ({ children }) => {
    const child = useRef<HTMLElement | null>(null)
    useEffect(() => {
        document.addEventListener('scroll', handleScroll)

        return () => {
            document.removeEventListener('scroll', handleScroll)
        }
    }, [])

    function handleScroll(event: Event) {
        const target = event.target as Document;
        const docElem = target.documentElement;

        if (docElem.scrollHeight - (docElem.scrollTop + window.innerWidth) < 100) {
            if(!child.current) return;
            child.current.classList.add('visible')
        }
    }

    return <div style={{position: "absolute", bottom: 0}} ref={child as RefObject<HTMLDivElement>}>{children}</div>
}
