import React, { useRef, useEffect, ReactNode, FC } from 'react';

interface FadeProps {
    children: React.ReactNode
}

export const Fade: FC<FadeProps> = ({children}) => {
  const ref = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      if (entries[0].isIntersecting) {
        console.log('Компонент виден');
      } else {
        console.log('Компонент не виден');
      }
    }, {
      threshold: 1.0
    });

    if (ref.current) {
      observer.observe(ref.current);
    }

    return () => {
      if (ref.current) {
        observer.unobserve(ref.current);
      }
    };
  }, []);

  return (
    <div ref={ref}>
      <p>Этот текст будет отображаться, если компонент виден</p>
    </div>
  );
}
