import { FC, ReactNode, useEffect, useRef } from "react"

interface FadeProps {
    children: ReactNode,
    startedVis: boolean,
}
export const Animation: FC<FadeProps> = ({ children, startedVis }) => {
    const child = useRef<HTMLElement | null>(null)
    useEffect(() => {
        if(startedVis){
            child.current?.classList.add('vis')
        }
    }, []);

    useEffect(() => {
        document.addEventListener('scroll', handleScroll)

        return () => {
            document.removeEventListener('scroll', handleScroll)
        }
    }, [])

    function handleScroll(event: Event) {
        const target = event.target as Document;
        const docElem = target.documentElement;

        if (docElem.scrollHeight - (docElem.scrollTop + window.innerWidth) < 500) {
            setVis(true)
        }
    }

    return (
        <>
            {
                vis && 
                <div >{children}</div>
            }
        </>
    )
}
