import React, { useRef, useState, useEffect } from 'react';

function VisibleComponent({ children }) {
  const ref = useRef(null);
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    const observer = new IntersectionObserver((entries) => {
      const isIntersecting = entries[0].isIntersecting;

      if (isIntersecting) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    }, {
      threshold: 0.5 // Компонент считается видимым, если хотя бы 50% его площади в viewport
    });

    if (ref.current) {
      observer.observe(ref.current);
    }

    return () => {
      if (ref.current) {
        observer.unobserve(ref.current);
      }
    };
  }, []);

  if (!isVisible) {
    return null; // Удаляем компонент из DOM
  }

  return (
    <div ref={ref}>
      {children}
    </div>
  );
}

// Пример использования
export default function App() {
  return (
    <div style={{ height: '2000px' }}>
      <div style={{ height: '1000px' }}></div>

      <VisibleComponent>
        <div style={{ height: '200px', backgroundColor: 'lightgreen' }}>
          Я появляюсь, когда вы прокручиваете до меня
        </div>
      </VisibleComponent>

      <div style={{ height: '1000px' }}></div>
    </div>
  );
}
